if (operation === 'add'){
    function addition(){
        let num3 = num1 + num2;
        console.log('The result is:', num3);    
    }
    addition(num1,num2)        
}
else if(operation === 'sub'){
    function subtraction(){
        let num3= num1 - num2;
        console.log("The result is:", num3);
    }
    subtraction(num1,num2);
}
else if(operation ==='mul'){
    function multiply(){
        let num3 = num1 * num2;
        console.log("The result is:",num3);
            }
            multiply(num1,num2);
}
else if(operation ==='div'){
    function divide(){
        let num3= num1/num2;
    console.log("The result is:", num3);
    }
    divide(num1,num2)
}


